DECLARE @killCommand NVARCHAR(MAX) = '';
DECLARE @databaseName NVARCHAR(128) = 'udacity'; -- Replace with your database name

-- Generate kill commands for all user sessions in the specified database
SELECT @killCommand = @killCommand + 'KILL ' + CAST(session_id AS VARCHAR(10)) + '; '
FROM sys.dm_exec_sessions
WHERE DB_NAME(database_id) = @databaseName
  AND is_user_process = 1;

-- Execute the kill commands
EXEC sp_executesql @killCommand;

-- Drop the database
DROP DATABASE udacity;
